                            <label>
                                <p className="teachernotice">Enter the link to the item:</p>
                                <input type="text" value={this.state.value} onChange={this.handleChange} />
                            </label>
                                <br /><br />
                            <input type="submit" value="Submit" />
                        </form>
                    </center>
                </div>
            );

            return ( //big modal
                <Modal
                    style= {{
                        overlay: {
                        position        : 'fixed',
                        top             : -150,
                        left            : 0,
                        right           : 0,
                        bottom          : 0,
                        zIndex          : 99999999,
                        overflow        : 'hidden',
                        perspective     :  1300,
                        backgroundColor : 'rgba(0, 0, 0, 0.3)'
                        },

                        content: {
                        position                : 'relative',
                        margin                  : '15% auto',
                        width                   : '60%',
                        height                  : '600px',
                        border                  : '1px solid rgba(0, 0, 0, .2)',
                        background              : '#fff',
                        borderRadius            : '4px',
                        outline                 : 'none',
                        boxShadow               : '0 5px 10px rgba(0, 0, 0, .3)',
                        textAlign               : 'center',
                        overflow                : 'hidden',
